<script lang="ts" setup>
import { useStore } from 'vuex'
import { computed, onMounted, ref, watch } from 'vue'
import type { Ref } from 'vue'
import { LogoModes } from '@/lib/logo-modes'
import { useAnimateLogo } from '@/lib/animations/use-animate-logo'

const logoElement: Ref<HTMLInputElement|null> = ref(null)
const { animateLogo } = useAnimateLogo(logoElement)
const store = useStore()
const logoModeCssClass = computed(() => {
  switch (store.state.ui.logoMode) {
    case LogoModes.ScreenMiddle:
      return 'screen-middle'

    case LogoModes.ScreenTop:
      return 'screen-top'

    default:
      return 'small-logo'
  }
})

watch(() => store.state.ui.isLogoAnimated, (isValue: boolean) => {
  if (isValue) {
    animateLogo()
  }
})

onMounted(() => {
  if (store.state.ui.isLogoAnimated) {
    animateLogo()
  }
})
</script>
